def min_removals_to_avoid_xxx(n, filename):
    filename_list = list(filename)
    count_x = 0
    positions_of_x = []

    for i, char in enumerate(filename_list):
        if char == 'x':
            count_x += 1
            positions_of_x.append(i)

    if count_x < 3:
        return 0

    removal_count = 0
    i = 0

    while i < len(positions_of_x) - 2:
        if positions_of_x[i+2] - positions_of_x[i] == 2:
            filename_list.pop(positions_of_x[i+1])
            count_x -= 1
            removal_count += 1
            positions_of_x.remove(positions_of_x[i+1])
            positions_of_x = [pos - 1 if pos > positions_of_x[i+1] else pos for pos in positions_of_x]
        else:
            i += 1

    return removal_count

t = int(input())
results = []

for _ in range(t):
    n = int(input())
    filename = input().strip()
    results.append(min_removals_to_avoid_xxx(n, filename))

for result in results:
    print(result)

