MOD = int(1e9 + 7)

def factorial(n):
    fact = [1] * (n + 1)
    for i in range(1, n + 1):
        fact[i] = fact[i - 1] * i % MOD
    return fact

def mod_inverse(a, p=MOD):
    return pow(a, p - 2, p)

def solve(n, k):
    fact = factorial(k + 1)
    inv_fact = [mod_inverse(fact[i], MOD) for i in range(k + 1)]

    dp = [[0] * (k + 1) for _ in range(n + 1)]
    for v in range(1, n + 1):
        dp[v][0] = v

    def get_divisors(v):
        divisors = []
        for i in range(1, int(v**0.5) + 1):
            if v % i == 0:
                divisors.append(i)
                if i != v // i:
                    divisors.append(v // i)
        return divisors

    for i in range(1, k + 1):
        for v in range(1, n + 1):
            divisors = get_divisors(v)
            num_divisors = len(divisors)
            dp[v][i] = sum(dp[d][i - 1] for d in divisors) * mod_inverse(num_divisors, MOD) % MOD

    return dp[n][k]

