def prime_factors(n):
    i = 2
    factors = set()
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            factors.add(i)
    if n > 1:
        factors.add(n)
    return factors

def g(x, p):
    count = 0
    while x % p == 0:
        x //= p
        count += 1
    return count

def f(x, y):
    ans = 1
    for p in prime_factors(x):
        ans *= pow(g(y, p), 1, MOD)
        ans %= MOD
    return ans

def solve(x, n):
    ans = 1
    for i in range(1, n + 1):
        ans *= f(x, i)
        ans %= MOD
    return ans

