def generate_hat_schedule(t):
    for _ in range(t):
        n, m, k = map(int, input().split())

        # Calculate the number of players in larger and smaller groups
        big_group_size = n // m + (n % m > 0)
        small_group_size = n // m

        # Initialize variables
        players = list(range(1, n + 1))
        big_groups = []
        small_groups = []

        # Distribute players into groups
        while len(players) > 0:
            if len(big_groups) < n // big_group_size:
                big_groups.append(big_group_size)
            else:
                small_groups.append(small_group_size)

            # Remove players assigned to the current group
            if len(big_groups) <= n // big_group_size:
                big_groups[-1] -= 1
                players.pop()
            else:
                small_groups[-1] -= 1
                players.pop()

        # Ensure the last group has the correct size
        if len(big_groups) == n // big_group_size:
            big_groups[-1] += n % big_group_size

        # Print the schedule
        for _ in range(k):
            for i in range(m):
                if i < len(big_groups) and big_groups[i] > 0:
                    print(big_groups[i], end=' ')
                    big_groups[i] -= 1
                else:
                    print(small_group_size, end=' ')
                    small_group_size -= 1
            print()


