        for _ in range(m):
            a = int(data[index]) - 1
            b = int(data[index + 1]) - 1
            index += 2
            edges.append((a, b))

        # Construct adjacency list
        adj = [[] for _ in range(n)]
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        visited = [False] * n
        domino_count = 0

        for node in range(n):
            if not visited[node]:
                stack = [node]
                while stack:
                    u = stack.pop()
                    if not visited[u]:
                        visited[u] = True
                        for v in adj[u]:
                            if not visited[v]:
                                stack.append(v)

                # Check if the connected component can be covered by dominoes
                if len(adj[u]) % 2 == 0:
                    domino_count += len(adj[u]) // 2

        results.append(domino_count)

    for result in results:
        print(result)

