def fair_hat_schedule(n, m, k):
    s = n // m
    t = n // m + (n % m != 0)
    z = n % m

    players = list(range(1, n + 1))

    for _ in range(k):
        group = []
        for i in range(m):
            if i < z:
                group.append(str(t) + ' ' + ' '.join(map(str, players[:t])))
                players = players[t:]
            else:
                group.append(str(s) + ' ' + ' '.join(map(str, players[:s])))
                players = players[s:]
        print('\n'.join(group))
        print()

