def mex_count_permutations(n, a):
    indices = sorted(range(n), key=lambda x: a[x])
    dp = [[0] * (n + 1) for _ in range(n)]
    dp[0][indices[0]] = 1

    for i in range(1, n):
        prefix_sum = [0] * (n + 1)
        for j in range(i):
            prefix_sum[j + 1] = prefix_sum[j]
            if dp[j][indices[i]]:
                prefix_sum[j + 1] += 1

        dp[i][indices[i]] = prefix_sum[i]
        for j in range(i):
            dp[i][indices[j]] = (dp[i][indices[j]] + dp[j][indices[i]]) % MOD

    return sum(dp[n - 1]) % MOD

