        if x not in colnum:
            colnum[x] = set()
            colnumcnt[x] = 0
            numcol[x] = i
        colnum[x].add(numcol[x] // m)
        colnumcnt[x] += 1

    q = [[] for _ in range(n)]

    for x in sorted(colnum):
        temp = list(colnum[x])
        for i in range(len(temp)):
            cur = temp.pop()
            c = 0
            for y in q[cur]:
                if y > numcol[x]:
                    c += 1
            q[cur].append(numcol[x])
            ans += c

    return ans

