def min_layers(N):
    segments = [(i, j) for i in range(N + 1) for j in range(i, N + 1)]
    segments.sort(key=lambda x: x[0])
    layers = []

    def add_to_layer(layer, segment):
        for existing_segment in layer:
            if not (segment[1] < existing_segment[0] or existing_segment[1] < segment[0]):
                return False
        return True

    for segment in segments:
        added = False
        for layer in layers:
            if add_to_layer(layer, segment):
                layer.append(segment)
                added = True
                break
        if not added:
            layers.append([segment])

    return len(layers)

