def hat_game_schedule(n, m, k):
    base = n // m
    extra = n % m
    result = []
    players = list(range(1, n + 1))

    for _ in range(k):
        table = []
        for i in range(m):
            if extra > 0:
                table.append(base + 1)
                extra -= 1
            else:
                table.append(base)

        for j in range(m):
            if table[j] == base + 1:
                if len(players) >= base + 1:
                    table[j].extend(players[:base + 1])
                    players = players[base + 1:]
                else:
                    table[j].extend(players)
                    players = []
            else:
                if len(players) >= base:
                    table[j].extend(players[:base])
                    players = players[base:]
                else:
                    table[j].extend(players)
                    players = []

        result.extend(table)

    return result

def similar_permutations(a):
    MOD = 10**9 + 7
    n = len(a)
    fact = [1] * (n + 1)
    inv_fact = [1] * (n + 1)
    for i in range(1, n + 1):
        fact[i] = fact[i - 1] * i % MOD
        inv_fact[i] = pow(fact[i], MOD - 2, MOD)

    def perm_count(arr):
        cnt = [0] * (len(arr) + 1)
        for x in arr:
            cnt[x] += 1
        ans = fact[len(arr)]
        for c in cnt:
            ans *= inv_fact[c]
            ans %= MOD
        return ans

    sorted_a = sorted(a)
    ans = 0
    for i in range(1, n + 1):
        subarr = [sorted_a[j] for j in range(i - 1, n, i)]
        ans += perm_count(subarr)
        ans %= MOD
    return ans

def minimize_inconvenience(n, m, a):
    a.sort()
    q = [[-1] * m for _ in range(n)]
    colnum = {}
    colnumcnt = {}
    numcol = {}

    for i in range(n * m):
        colnum[a[i]] = i // m
        colnumcnt[a[i]] = i // m
        numcol[colnumcnt[a[i]]] = set()

    ans = 0
    for x in a:
        temp = numcol[colnumcnt[x]]
        for r in temp:
            q[r][colnumcnt[x]] = x
            ans += colnumcnt[x]
        if colnumcnt[x] < m:
            colnumcnt[x] += 1
        else:
            del numcol[colnumcnt[x]]
            numcol[colnumcnt[x] - 1].add(colnum[x])

    return ans

