MOD = 10**9 + 7

def generate_hat_schedule(t):
    results = []
    for _ in range(t):
        n, m, k = map(int, input().split())
        ceil_group_size = math.ceil(n / m)
        floor_group_size = math.floor(n / m)
        players = list(range(1, n + 1))
        for _ in range(k):
            group_indices = [i // floor_group_size for i in range(n)]
            count = [group_indices.count(i) for i in range(m)]
            extra_count = len(players) - sum(count)
            for i in range(extra_count):
                count[i % m] += 1
            schedule = []
            for i in range(m):
                start = sum(count[:i])
                end = start + count[i]
                schedule.append((count[i], *players[start:end]))
            results.extend(schedule)
        for line in results:
            print(line[0], *line[1:])
        results.clear()
        print()

