def fair_schedule(n, m, k):
    base_size = n // m
    extra_players = n % m
    schedule = []
    players = list(range(1, n + 1))
    for _ in range(k):
        current_table = []
        for i in range(m):
            if extra_players > 0:
                current_table.append(base_size + 1)
                extra_players -= 1
            else:
                current_table.append(base_size)
        for player in players:
            current_table.append(player)
        import random
        random.shuffle(current_table)
        schedule.extend(current_table)
    return schedule

def count_similar_permutations(a):
    from itertools import combinations
    from collections import Counter
    n = len(a)
    MOD = 10**9 + 7
    def mex(subarr):
        seen = set(subarr)
        x = 0
        while x in seen:
            x += 1
        return x
    mex_counts = [Counter() for _ in range(n)]
    for l in range(n):
        current_mex = 0
        for r in range(l, n):
            current_mex |= (1 << a[r])
            mex_counts[r].update({current_mex: 1})
    result = 1
    for v in mex_counts[-1].values():
        result *= v
        result %= MOD
    return result

def minimize_inconvenience(n, m, A):
    MOD = 10**9 + 7
    A.sort()
    colnum = {}
    colnumcnt = {}
    numcol = {}
    for i, a in enumerate(A):
        if a not in colnum:
            colnum[a] = []
            colnumcnt[a] = 0
            numcol[i] = a
    for i, a in enumerate(A):
        colnum[a].append(i)
    q = [set() for _ in range(m)]
    ans = 0
    for a in A:
        temp = [i for i in colnum[a]]
        for i in temp:
            while q[i]:
                ans += len(q[i])
            colnumcnt[a] += 1
            colnum[a].remove(i)
            q[i].add(colnumcnt[a])
    return ans

def maximize_trailing_nines(p, d):
    p_str = str(p)
    d = int(d)
    while d > 0 and p_str[-1] == '9':
        p_str = str(int(p_str) - 1)
        d -= 1
    return int(p_str)

def main():
    import sys
    input = sys.stdin.read
    data = input().split()
    index = 0
    t = int(data[index])
    index += 1
    results = []
    for _ in range(t):
        n = int(data[index])
        m = int(data[index + 1])
        k = int(data[index + 2])
        index += 3
        if n == m:
            schedule = [list(range(1, n + 1)) for _ in range(k)]
        else:
            schedule = fair_schedule(n, m, k)
        if n == 1:
            similar_permutations = 1
        else:
            similar_permutations = count_similar_permutations([i for i in range(n)])
        inconvenience = minimize_inconvenience(n, m, [int(data[index + i]) for i in range(n * m)])
        index += n * m
        max_nines_price = maximize_trailing_nines(int(data[index]), int(data[index + 1]))
        index += 2
        results.append(schedule)
        results.append(similar_permutations)
        results.append(inconvenience)
        results.append(max_nines_price)
    for result in results:
        if isinstance(result, list):
            print(" ".join(map(str, result)))
        else:
            print(result)

if __name__ == "__main__":
