    a.sort()
    k = 1
    r = 0
    n_min = n_max = a[0]

    for i in range(1, n):
        if n < a[i] < r:
            k = (k * (r - n - i + 1)) % MOD
        n_min = min(n_min, a[i])
        r = max(r, a[i])

    return k

# Problem 3: Minimize Total Inconvenience
def minimize_inconvenience(n, m, A):
    ans = 0
    colnum = {}
    colnumcnt = {}
    numcol = {}

    for i in range(len(A)):
        colnum[i] = (A[i], (i // m) + 1)
        colnumcnt[A[i]] = colnumcnt.get(A[i], 0) + 1
        numcol.setdefault((i // m) + 1, set()).add(A[i])

    q = [[] for _ in range(m)]

    for v in sorted(colnum.keys()):
        temp = numcol[colnum[v][1]]
        for i in range(n):
            if colnum[i][0] == v:
                while q[(i // m)].count(v):
                    q[(i // m)].remove(v)
                q[(i // m)].append(v)
                if q[(i // m)].count(v):
                    ans += len(q[(i // m)]) - q[(i // m)].index(v) - 1
                break

    return ans

