def count_unquestionably_lucky_numbers(n, x, y):
    dp = [[0] * 2 for _ in range(10)]
    dp[0][0] = 1

    for digit in str(n):
        ndp = [[0] * 2 for _ in range(10)]
        for d in range(10):
            if d == int(digit):
                for i in range(2):
                    ndp[d][i] = (ndp[d][i] + dp[d][i]) % MOD
                    ndp[x][i] = (ndp[x][i] + dp[d][i]) % MOD
            elif d == x:
                for i in range(2):
                    ndp[x][i] = (ndp[x][i] + dp[d][i]) % MOD
                    ndp[y][i] = (ndp[y][i] + dp[d][i]) % MOD
            elif d == y:
                for i in range(2):
                    ndp[y][i] = (ndp[y][i] + dp[d][i]) % MOD

        dp = ndp

    return (dp[0][0] + dp[x][0] + dp[y][0]) % MOD

