def count_empty_cells(n):
    total_cells = (2 * n) ** 2
    occupied_cells = set()

    for k in range(n, 1, -1):
        num_cookies_width = 2 * n // k
        num_cookies_height = 2 * n // k

        for i in range(0, 2 * n, k):
            for j in range(0, 2 * n, k):
                for x in range(k):
                    for y in range(k):
                        occupied_cells.add((i + x, j + y))

    empty_cells = total_cells - len(occupied_cells)

    return empty_cells % (10**6 + 3)

# Input handling
t = int(input())
results = []
for _ in range(t):
    n = int(input())
    results.append(count_empty_cells(n))

# Output results
for result in results:
    print(result)

