def reflect_number(n):
    return int(''.join(str(9 - int(digit)) for digit in str(n)))

def max_weight(l, r):
    max_weight = 0
    for num in range(l, r + 1):
        reflection = reflect_number(num)
        weight = num * reflection
        if weight > max_weight:
            max_weight = weight
    return max_weight

# Read input
t = int(input().strip())
results = []

for _ in range(t):
    l, r = map(int, input().strip().split())
    results.append(max_weight(l, r))

# Output results
for result in results:
    print(result)

