def check(n, a, b, p):
    return (n * pow(a, n, p)) % p == b

def main():
    a, b, p, x = map(int, input().split())
    count = 0
    for n in range(1, x + 1):
        if check(n, a, b, p):
            count += 1
    print(count)

if __name__ == "__main__":
    main()

MOD = 998244353

def mod_inv(a, m=MOD):
    return pow(a, m - 2, m)

def binom(n, k):
    if k > n:
        return 0
    num = den = 1
    for i in range(k):
        num = num * (n - i) % MOD
        den = den * (i + 1) % MOD
    return num * mod_inv(den, MOD) % MOD

def solve(p, s, r):
    dp = [[0] * (s + 1) for _ in range(p + 1)]
    dp[0][0] = 1

    for i in range(1, p + 1):
        for j in range(s + 1):
            dp[i][j] = dp[i - 1][j]
            if j >= r:
                dp[i][j] += dp[i - 1][j - r]
                dp[i][j] %= MOD

    total_ways = binom(s + p - 1, p - 1)

    win_ways = 0
    for j in range(r, s + 1):
        if dp[p][j] > 0:
            win_ways += dp[p][j] * binom(s - j, p - 1)
            win_ways %= MOD

    prob = win_ways * mod_inv(total_ways, MOD) % MOD
    return prob

# Read input
p, s, r = map(int, input().split())

# Calculate and print the result
result = solve(p, s, r)
print(result)
