def can_say_hello(s):
    hello_index = 0
    for char in s:
        if hello_index < len("hello") and char == "hello"[hello_index]:
            hello_index += 1
    return "YES" if hello_index == len("hello") else "NO"

# Take input from standard input
input_string = input()
result = can_say_hello(input_string)
print(result)
