def nth_suitable_number(N):
    count = 0
    x = 0

    while True:
        x += 1
        a = 1
        while a * a < x:
            if x % a == 0:
                b = x // a
                if a < b:
                    count += 1
                    if count == N:
                        return x
            a += 1

