def power(x, y):
    res = 1
    x = x % MOD
    while y > 0:
        if y & 1:
            res = (res * x) % MOD
        y = y >> 1
        x = (x * x) % MOD
    return res

def inverse(x):
    return power(x, MOD - 2)

