    n, m, p = map(int, input().split())

    # dp[i][j][k] will store the number of ways to arrange i participants
    # with j unoccupied computers and k participants who have been seated
    dp = [[[0 for _ in range(m + 1)] for _ in range(n + 1)] for _ in range(m + 1)]

    dp[0][0][0] = 1

    for i in range(1, m + 1):
        for j in range(n + 1):
            for k in range(m + 1):
                dp[i][j][k] = dp[i - 1][j][k] * (j + 1) % MOD
                if k > 0:
                    dp[i][j][k] = (dp[i][j][k] + dp[i - 1][j - 1][k - 1] * (n - j + 1) % MOD) % MOD

    total_ways = dp[m][n][m]
    total_ways = (total_ways * power(power(2, m), MOD - 2)) % MOD

    total_madness = 0
    for i in range(1, m + 1):
        for j in range(n + 1):
            for k in range(m + 1):
                total_madness = (total_madness + dp[i][j][k] * (i - k) % MOD) % MOD

    result = (total_ways * total_madness) % MOD
    print(result)

# Read the number of test cases
