def max_dominoes(n, m, edges):
    from collections import defaultdict as dd

    # Build the adjacency list representation of the graph
    adj = dd(list)
    for u, v in edges:
        adj[u].append(v)
        adj[v].append(u)

    def dfs(node, parent):
        stack = [node]
        visited[node] = True
        while stack:
            node = stack.pop()
            for neighbor in adj[node]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    stack.append(neighbor)

    # Perform DFS to count connected components
    component_sizes = []
    visited = [False] * (n + 1)
    for node in range(1, n + 1):
        if not visited[node]:
            dfs(node, None)
            component_sizes.append(sum(visited[1:n+1]))

    # Sum up the largest even numbers from the component sizes
    res = 0
    for size in sorted(component_sizes, reverse=True):
        if size >= 2:
            res += 2
            size -= 2

    return res

