def modinv(x, MOD):
    return pow(x, MOD-2, MOD)

def calc(n, k, l):
    def comb(n, k):
        res = 1
        for i in range(k):
            res = res * (n - i) * modinv(i + 1, MOD) % MOD
        return res

    def calc_comb_sum(n, k):
        if k > n:
            return 0
        return comb(n, k)

    def calc_expectation(n, k):
        if k == 0:
            return 0
        if k == n:
            return l
        if n == 1:
            return 0

        exp = 0
        for i in range(k, n + 1):
            exp += comb(n, i) * calc_comb_sum(i, k) * (l / n) ** i * ((1 - l / n) ** (n - i))
            exp %= MOD
        return exp

    return calc_expectation(n * 2, k)

