def count_cyclical_strings(n, s):
    def powmod(x, y, mod):
        res = 1
        x = x % mod
        while y > 0:
            if y % 2 == 1:
                res = (res * x) % mod
            y = y >> 1
            x = (x * x) % mod
        return res

    def choose(n, k, mod):
        if k > n:
            return 0
        numerator = 1
        denominator = 1
        for i in range(k):
            numerator = (numerator * (n - i)) % mod
            denominator = (denominator * (i + 1)) % mod
        return numerator * powmod(denominator, mod - 2, mod) % mod

    def count_substrings(s, t):
        n, m = len(s), len(t)
        dp = [0] * (m + 1)
        dp[0] = 1
        for char in s:
            ndp = [0] * (m + 1)
            for j in range(m, -1, -1):
                if j >= len(char):
                    ndp[j] = (ndp[j] + dp[j - len(char)]) % MOD
                else:
                    ndp[j] = dp[j]
            dp = ndp
        return dp[m]

    m = len(s)
    dp = [[0] * (1 << m) for _ in range(n + 1)]
    dp[0][0] = 1
    for i in range(n):
        for mask in range(1 << m):
            for j in range(m):
                if mask & (1 << j):
                    nxt_mask = mask ^ (1 << j)
                    dp[i + 1][nxt_mask] = (dp[i + 1][nxt_mask] + dp[i][mask]) % MOD

    ans = 0
    for mask in range(1 << m):
        sub_len = bin(mask).count('1')
        if sub_len < m:
            cnt = count_substrings(s, bin(mask)[2:])
            ans = (ans + dp[n][mask] * powmod(cnt, m - sub_len, MOD)) % MOD
        else:
            ans = (ans + dp[n][mask]) % MOD

    return ans

