MOD = 998244353

def power(x, y, p):
    res = 1
    x = x % p
    while y > 0:
        if y & 1:
            res = (res * x) % p
        y = y >> 1
        x = (x * x) % p
    return res

def factorial(n, p):
    fact = [1] * (n + 1)
    for i in range(1, n + 1):
        fact[i] = (fact[i - 1] * i) % p
    return fact

def inverse_factorial(n, p):
    inv_fact = [1] * (n + 1)
    inv_fact[n] = power(factorial(n, p), p - 2, p)
    for i in range(n, 0, -1):
        inv_fact[i - 1] = (inv_fact[i] * i) % p
    return inv_fact

def binomial_coefficient(n, k, p):
    return (factorial(n, p) * inverse_factorial(k, p) * inverse_factorial(n - k, p)) % p

def solve(n):
    if n % 2 == 0:
        k = n // 2
        even = k * (k - 1) // 2
        odd = k * (n - k) // 2
        result = (power(2, even, MOD) * power(2, odd, MOD) * 2) % MOD
    else:
        half = (n - 1) // 2
        even = half * (half - 1) // 2
        odd = half * (n - half) // 2
        middle = n // 2
        result = (power(2, even, MOD) * power(2, odd, MOD) * power(2, middle, MOD)) % MOD
    return result

def main():
    n = int(input())
    print(solve(n))

if __name__ == "__main__":
    main()

