def findGoldenAge(x, y, l, r):
    def is_unlucky(n):
        return n % x == 0 or n % y == 0

    max_length = 0
    current_start = l

    for i in range(l, r + 1):
        if is_unlucky(i):
            max_length = max(max_length, i - current_start)
            current_start = i + 1

    # Check the last segment
    if not is_unlucky(current_start):
        max_length = max(max_length, r - current_start + 1)

    return max_length

# Read the number of test cases
t = int(input())
results = []

# Process each test case
for _ in range(t):
    x, y, l, r = map(int, input().split())
    results.append(findGoldenAge(x, y, l, r))

# Output the results for all test cases
for result in results:
    print(result)
