def max_correct_commands(n, commands):
    x, y = 0, 0
    correct_count = 0

    for command in commands:
        if command == 'U':
            y += 1
        elif command == 'D':
            y -= 1
        elif command == 'L':
            x -= 1
        elif command == 'R':
            x += 1

        if x == 0 and y == 0:
            correct_count += 1

    return correct_count

# Read input
t = int(input())
results = []

for _ in range(t):
    n = int(input())
    commands = input()
    results.append(max_correct_commands(n, commands))

# Output results
for result in results:
    print(result)

