def count_years_with_one_zero(a, b):
    def count_numbers_with_one_zero_in_range(start, end):
        low = bin(start)[2:].count('0')
        high = bin(end)[2:].count('0')
        return high - low

    def count_numbers_with_one_zero_in_power_of_two(n):
        if n == 0:
            return 0
        power = (n & (n - 1)).bit_length() - 1
        count = 2 ** power
        if n >= 2 ** power + 1:
            count += count_numbers_with_one_zero_in_range(2 ** power + 1, n)
        return count

    return count_numbers_with_one_zero_in_power_of_two(b) - count_numbers_with_one_zero_in_power_of_two(a - 1)

# Read input values
a, b = map(int, input().split())

