def count_return_substrings(commands):
    n = len(commands)
    count = 0
    x, y = 0, 0

    prefix_sum = {(0, 0): 1}
    cur_x, cur_y = 0, 0

    for command in commands:
        if command == 'U':
            cur_y += 1
        elif command == 'D':
            cur_y -= 1
        elif command == 'L':
            cur_x -= 1
        elif command == 'R':
            cur_x += 1

        target = (cur_x, cur_y)
        if target in prefix_sum:
            count += prefix_sum[target]

        prefix_sum[(cur_x, cur_y)] = prefix_sum.get((cur_x, cur_y), 0) + 1

    return count

