MOD = 1000000007

def count_ways(n, p):
    if p < 3 or n > p:
        return 0

    dp = [[0] * (p + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    for i in range(1, n + 1):
        prefix_sum = [0] * (p + 1)
        for j in range(p + 1):
            prefix_sum[j] = prefix_sum[j - 1] + dp[i - 1][j]

        for j in range(3, p + 1):
            dp[i][j] = (prefix_sum[j - 1] - prefix_sum[j - 3]) % MOD

    return dp[n][p]

# Read input
t = int(input())
results = []

for _ in range(t):
    n, p = map(int, input().split())
    results.append(count_ways(n, p))

# Print results
for result in results:
    print(result)
