def min_shoes_to_change(n, m, grid):
    moves = 0
    visited = [[False] * m for _ in range(n)]

    def dfs(i, j):
        stack = [(i, j)]
        count = 0
        while stack:
            x, y = stack.pop()
            if visited[x][y]:
                continue
            visited[x][y] = True
            count += 1
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < n and 0 <= ny < m and not visited[nx][ny] and grid[nx][ny] == grid[x][y]:
                    stack.append((nx, ny))
        return count

    for i in range(n):
        for j in range(m):
            if not visited[i][j]:
                size = dfs(i, j)
                moves += (size - 1) // 2

    return moves

