def maxOnesAfterFlip(n, a):
    # Function to count the number of 1s in the array
    def countOnes():
        return a.count(1)

    # Initial count of 1s
    maxOnes = countOnes()

    # Try flipping every possible segment
    for i in range(n):
        for j in range(i, n):
            # Flip the segment from i to j
            for k in range(i, j + 1):
                a[k] = 1 - a[k]

            # Count the number of 1s after flipping
            currentOnes = countOnes()

            # Update the maximum count of 1s
            if currentOnes > maxOnes:
                maxOnes = currentOnes

            # Flip back the segment from i to j to restore the original array
            for k in range(i, j + 1):
                a[k] = 1 - a[k]

    return maxOnes

