def check_points(points):
    x_coords = sorted({point[0] for point in points})
    y_coords = sorted({point[1] for point in points})

    if len(x_coords) != 3 or len(y_coords) != 3:
        return "ugly"

    expected_points = {(x_coords[i], y_coords[j]) for i in range(3) for j in range(3)}
    actual_points = set(points)

    if (x_coords[1], y_coords[1]) in actual_points:
        return "ugly"

    if expected_points.difference(actual_points) == {(x_coords[1], y_coords[1])}:
        return "respectable"

    return "ugly"

