def can_press_all Panels(k, panel_table):
    # Flatten the panel table into a list of panel times
    panel_times = [int(panel) for row in panel_table for panel in row if panel != '.']

    # Sort the panel times
    panel_times.sort()

    # Count occurrences of each panel time
    time_count = {}
    for time in panel_times:
        if time in time_count:
            time_count[time] += 1
        else:
            time_count[time] = 1

    # Check if we can press all panels within k presses per time
    for count in time_count.values():
        if count > k:
            return "NO"

    return "YES"

