def min_insertions(s):
    n = len(s)
    dp = [0] * n
    dp[0] = 1

    for i in range(1, n):
        if s[i] == s[i - 1]:
            dp[i] = dp[i - 1] + 1
        else:
            dp[i] = 1

    return max(dp) // 2

