def max_number_with_swaps(number, k):
    num_str = str(number)
    num_list = list(num_str)
    length = len(num_list)

    for i in range(length):
        max_digit = -1
        max_index = -1
        for j in range(i, min(i + k + 1, length)):
            if int(num_list[j]) > max_digit:
                max_digit = int(num_list[j])
                max_index = j

        if max_index == -1:
            break

        num_list[max_index], num_list[i] = num_list[i], num_list[max_index]
        k -= (max_index - i)

    return ''.join(num_list)

# Read input values
a, k = map(int, input().split())

