    def factorial(x):
        if x == 0 or x == 1:
            return 1
        else:
            return x * factorial(x - 1)

    def nth_permutation(n, k):
        k -= 1
        digits = list(range(1, n + 1))
        perm = ""
        fact = factorial(n - 1)
        while n > 0:
            idx, k = divmod(k, fact)
            perm += str(digits.pop(idx))
            n -= 1
            if n > 0:
                fact //= n
        return perm

