def min_characters_to_remove(n, filename):
    remove_count = 0
    i = 0

    while i < n:
        if filename[i] == 'x':
            j = i + 1
            while j < n and filename[j] == 'x':
                j += 1
            if j - i >= 3:
                remove_count += (j - i - 2)
            i = j
        else:
            i += 1

    return remove_count

# Read input
n = int(input())
filename = input().strip()

# Call the function and print the result
print(min_characters_to_remove(n, filename))

