                        start -= 1
                else:
                    start = a_i
                    while start < n and new_computers[start]:
                        start += 1

                if not new_computers[start]:
                    new_computers[start] = True
                    backtrack(index + 1, (current_madness + abs(start - a_i)) % p, new_computers)

    backtrack(0, 0, [False] * (n + 1))
    return total_madness

