import math

def mod_inverse(x, m):
    return pow(x, m - 2, m)

def expected_value(n, k):
    if n == 1:
        return 1

    div_sum = 0
    for i in range(1, int(math.sqrt(n)) + 1):
        if n % i == 0:
            div_sum += i
            if i != n // i:
                div_sum += n // i

    gcd = math.gcd(div_sum, k)
    P = div_sum // gcd
    Q = k // gcd

    return (P * mod_inverse(Q, 10**9 + 7)) % (10**9 + 7)

# Read input
n, k = map(int, input().split())

# Output result
print(expected_value(n, k))

