def prime_factors(n):
    i = 2
    factors = set()
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            factors.add(i)
    if n > 1:
        factors.add(n)
    return factors

def g(x, p):
    k = 0
    while x % (p ** (k + 1)) == 0:
        k += 1
    return p ** k

def f(x, y):
    factors = prime_factors(x)
    result = 1
    for p in factors:
        result *= g(y, p)
        result %= MOD
    return result

def solve():
    x, n = map(int, input().split())
    result = 1
    for i in range(1, n + 1):
        result *= f(x, i)
        result %= MOD
    print(result)

if __name__ == "__main__":
