def find_pair(r):
    if r < 1:
        return "NO"

    for x in range(1, int(r**0.5) + 2):
        # Rearrange the equation to solve for y: y = ((r - x^2 - x) / (2x + 1))
        if (r - x*x - x) % (2*x + 1) == 0:
            y = (r - x*x - x) // (2*x + 1)
            if y > 0:
                return f"{x} {y}"

    return "NO"

# Read input
r = int(input())

# Call the function and print the result
print(find_pair(r))

