def max_correct_commands(n, commands):
    left_right = 0
    up_down = 0

    for command in commands:
        if command == 'L':
            left_right += 1
        elif command == 'R':
            left_right -= 1
        elif command == 'U':
            up_down += 1
        elif command == 'D':
            up_down -= 1

    # Ensure the robot returns to the starting cell by correcting the imbalances
    while left_right != 0:
        if left_right > 0:
            left_right -= 1
        else:
            left_right += 1
    while up_down != 0:
        if up_down > 0:
            up_down -= 1
        else:
            up_down += 1

    return len(commands) - abs(left_right + up_down)

