def max_ones_after_flip(n, arr):
    max_ones = sum(arr)
    for i in range(n):
        current_sum = sum(arr[:i])
        for j in range(i, n):
            if arr[j] == 0:
                current_sum += 1
            else:
                current_sum -= 1
            max_ones = max(max_ones, current_sum)
    return max_ones

