def max_number_with_swaps(a, k):
    digits = list(str(a))
    n = len(digits)

    for i in range(n):
        max_idx = i
        for j in range(i + 1, min(n, i + k + 1)):
            if digits[j] > digits[max_idx]:
                max_idx = j
        if max_idx != i:
            digits[i], digits[max_idx] = digits[max_idx], digits[i]
            k -= (max_idx - i)
            if k == 0:
                break

    return ''.join(digits)

