def count_camels(n, t):
    if n < t + 1 or t == 0:
        return 0

    def backtrack(y, index, up_count, down_count):
        if index == n:
            return 1

        count = 0
        for next_y in range(1, 5):
            if next_y != y[index - 1]:
                if (next_y > y[index - 1] and up_count < t - 1) or \
                   (next_y < y[index - 1] and down_count < t):
                    count += backtrack(next_y, index + 1, 
                                      up_count + (next_y > y[index - 1]), 
                                      down_count + (next_y < y[index - 1]))

        return count

    return backtrack([0] * n, 1, 0, 0)

# Read input
n, t = map(int, input().split())

# Output result
print(count_camels(n, t))

