        for existing_segment in layer:
            if not (segment[1] < existing_segment[0] or existing_segment[1] < segment[0]):
                return False
        return True

    for segment in segments:
        added = False
        for layer in layers:
            if add_to_layer(layer, segment):
                layer.append(segment)
                added = True
                break
        if not added:
            layers.append([segment])

    return len(layers)

