# Problem 1: Fair Schedule for Hat Game
def fair_schedule(n, m, k):
    floor = n // m
    ceil = n // m + (n % m > 0)
    players = list(range(1, n + 1))

    for _ in range(k):
        schedule = []
        for i in range(m):
            if i < n % m:
                group_size = ceil
            else:
                group_size = floor
            group = [players.pop(0)]
            while len(group) < group_size:
                group.append(players.pop(0))
            schedule.append(f"{group_size} {' '.join(map(str, group))}")
        yield '\n'.join(schedule)

