def sum_beauty(n):
    def fact(x):
        return pow(x, MOD - 2, MOD)

    def choose(x, y):
        return (fact(x) * fact(y) * fact(x - y)) % MOD

    def beauty(n):
        return (choose(n, 2) ** 2) % MOD

    return (sum(beauty(i) for i in range(1, n + 1))) % MOD

