from collections import defaultdict

def count_possible_codes(hint):
    letter_positions = defaultdict(set)
    group_digits = defaultdict(set)

    n = len(hint)

    for i, char in enumerate(hint):
        if char.isdigit():
            group_digits[char].add(int(char))
            letter_positions[char].add(i)
        elif char.isalpha():
            group_digits[char].add(-1)
            letter_positions[char].add(i)

    def product(counts):
        result = 1
        for count in counts:
            result *= count
        return result

    total_combinations = product(len(group_digits[group]) for group in group_digits if group.isalpha())

    if hint[0] == '?':
        total_combinations *= 9

    return str(total_combinations)

